function feedbeep( correct )% FEEDBEEP  Give feedback beep%% feedbeep( correct )% 29-Nov-1998 -- created (RFM)% 20-Feb-2013 -- modified to use persistent audioplayer objects, to%                eliminate short pause before beeps (RFM)persistent yesbeep nobeep% create audioplayersif isempty(yesbeep) || isnan(correct)    % yes beep    yeswave = nicebeep( '3e', 125, 0.25, 0 );    yesbeep = audioplayer( yeswave, 8192 );        % no beep    nowave = nicebeep( '2a', 125, 0.35, 0 );    nobeep = audioplayer( nowave, 8192 );    end% return if just initializingif isnan(correct)    returnend% play a beepif isnan(correct)    returnelseif correct    play(yesbeep);else    play(nobeep);end% nicebeep(ifso(correct,'3a','2a'),125,ifso(correct,0.25,1.00));return