function graphfont( fname, fsize, fweight )% GRAPHFONT  Set the font of a graph's axis labels and title%% graphfont( name, size, weight )% 06-Sep-98 -- created (RFM)% set default argumentsdefarg('fname','');defarg('fsize','');defarg('fweight','');% default settingif nargin==0,	fname='palatino';	fsize=12;	fweight='bold';end% convert font size to integerif isstr(fsize),	fsize=str2num(fsize);end% font nameif ~isempty(fname),	set(gca,'FontName',fname);	set(get(gca,'XLabel'),'FontName',fname);	set(get(gca,'YLabel'),'FontName',fname);	set(get(gca,'ZLabel'),'FontName',fname);	set(get(gca,'Title'),'FontName',fname);end% font sizeif ~isempty(fsize),	set(gca,'FontSize',fsize);	set(get(gca,'XLabel'),'FontSize',fsize);	set(get(gca,'YLabel'),'FontSize',fsize);	set(get(gca,'ZLabel'),'FontSize',fsize);	set(get(gca,'Title'),'FontSize',fsize);end% font weightif ~isempty(fweight),	set(gca,'FontWeight',fweight);	set(get(gca,'XLabel'),'FontWeight',fweight);	set(get(gca,'YLabel'),'FontWeight',fweight);	set(get(gca,'ZLabel'),'FontWeight',fweight);	set(get(gca,'Title'),'FontWeight',fweight);endreturn