function phys2mach( varargin )% PHYS2MATCH  Convert degrees to pixels and seconds to frames% % phys2mach( arg1P, arg2D, ... )% sometime 1999 -- created (RFM)% step through argumentsfor i=1:nargin,	v=varargin{i};	n=inputname(i);		% convert physical units to machine units, according to unit tag	% appended to variable name	switch n(end),		case 'D', evalin('caller',sprintf('%sP=%f;',n(1:end-1),deg2pixel(v)));		case 'S', evalin('caller',sprintf('%sF=%f;',n(1:end-1),sec2frame(v)));	end	endreturn