function p = getconfig( param )% GETCONFIG  Get environment configuration parameters%% p = getconfig( param )%% valid parameters%     - machine%     - screennum%     - monitor%     - monitormode%     - viewdistance%     - screensize%     - pixelsize%     - pixelperdeg%     - framerate%     - fgrgb, bgrgb%     - fglum, bglum% 29-Apr-1998 - created (RFM)% 06-Nov-2007 -- rewritten for native32b mode (RFM)% check initializationif isempty(whos('global','ENVIRON')) && strcmp(lower(param),'init')==0    warning('setconfig not initialized; initializing now');    setconfig('init');end% get environment variableglobal ENVIRONswitch lower(param),	case 'machine',      p=ENVIRON.machine;	case 'screennum',    p=ENVIRON.monitor.screennum;	case 'monitor',      p=ENVIRON.monitor;	case 'monitormode',  p=ENVIRON.monitor.mode;	case 'viewdistance', p=ENVIRON.viewdistance;	case 'screensize',   p=ENVIRON.monitor.screensize;	case 'pixelsize',    p=ENVIRON.monitor.pixelsize;	case 'pixelperdeg',  p=ENVIRON.pixelperdeg;	case 'framerate',    p=ENVIRON.monitor.framerate;	case 'fgrgb',        p=ENVIRON.fgrgb;	case 'bgrgb',        p=ENVIRON.bgrgb;	case 'fglum',        p=rgb2lum(ENVIRON.fgrgb);	case 'bglum',        p=rgb2lum(ENVIRON.bgrgb);	otherwise error( sprintf('Unknown parameter ''%s''',param) );endreturn